mapper.select:
    
    select
    
   		
   	
    
      distinct
    
    
    from tb_device
    
   		
			AND device_type =#{deviceType}
		
		
			AND imei =#{imei}
		
   		
			AND imei like #{imeiLike}
		
   		
			AND sn =#{sn}
		
   		
			AND sn like #{snLike}
		
   		
			AND device_status =#{deviceStatus}
		
		
			AND device_status !=#{deviceStatusNotEquals}
		
		
			AND target_no =#{targetNo}
		
   		
			AND target_no like #{targetNoLike}
		
		
			AND sim_mobile =#{simMobile}
		
   		
			AND sim_mobile like #{simMobileLike}
		
   		
			AND user_id =#{userId}
		
   		
			AND user_id like #{userIdLike}
		
		
    
    
        
	      order by #{orderByClause}
	    
   	
   	
        
	        
		      order by device_type
		    
	        
		      order by datestamp desc


select 

from tb_device
where device_type = #{deviceType,jdbcType=NVARCHAR}
  and imei = #{imei,jdbcType=NVARCHAR}
  and sn = #{sn,jdbcType=NVARCHAR}

mapper.delete:

delete from tb_device
where device_type = #{deviceType,jdbcType=NVARCHAR}
  and imei = #{imei,jdbcType=NVARCHAR}
  and sn = #{sn,jdbcType=NVARCHAR}

mapper.select:
    
    select count(*) from tb_device
     
   		
			AND device_type =#{deviceType}
		
   		
			AND imei = #{imei}
		
   		
			AND imei like #{imeiLike}
		
   		
			AND sn =#{sn}
		
   		
			AND sn like #{snLike}
		
   		
			AND device_status =#{deviceStatus}
		
   		
			AND user_id =#{userId}
		
		
			AND user_id like #{userIdLike}
