mapper.select:

select 

from tb_device
where device_type = #{deviceType,jdbcType=NVARCHAR}
  and imei = #{imei,jdbcType=NVARCHAR}
  and sn = #{sn,jdbcType=NVARCHAR}

mapper.delete:

delete from tb_device
where device_type = #{deviceType,jdbcType=NVARCHAR}
  and imei = #{imei,jdbcType=NVARCHAR}
  and sn = #{sn,jdbcType=NVARCHAR}
