mapper.select:
    
    select
    
   		
   	
    
      distinct
    
    
    from tb_news
    
        
            and seq_no = #{seqNo}
        
        
            and msg_class = #{msgClass}
        
        
            and push_status = #{pushStatus}
        
        
            and title like #{titleLike}
        
        
            and title = #{title}
        
    
    
        
	        
		      order by seq_no
		    
		    
		      order by datestamp desc


select count(*) from tb_news

    
        and msg_class = #{msgClass}
    
    
        and push_status = #{pushStatus}
    
    
        and title like #{titleLike}
    
    
        and title = #{title}

mapper.update:
       update tb_news
			  	         set push_status = 'W'
			  	       where seq_no =  #{seqNo}
			  	         and push_status = 'N';
